mapper.select:
	select 
	
	from vp_gdn
	where 1=1
	
		and doc_code = #{docCode,jdbcType=VARCHAR}
	
	
		and unit_code = #{unitCode,jdbcType=VARCHAR}
	
	
		and wareh_code = #{warehCode,jdbcType=VARCHAR}
	
	
		and src_doc_tye = #{srcDocTye,jdbcType=VARCHAR}
	
	
		and src_doc_code = #{srcDocCode,jdbcType=VARCHAR}
	
	
		and substr(disp_time,1,7) = #{queryMonth,jdbcType=VARCHAR}
	
	
		and substr(disp_time,1,10) = #{queryDay,jdbcType=VARCHAR}
	
  
		and disp_time >= #{dispTime1,jdbcType=VARCHAR}
	
	
		and disp_time <= #{dispTime2,jdbcType=VARCHAR}
	
	order by disp_time desc

  select count(1)
	from vp_gdn
	where 1=1
	
		and doc_code = #{docCode,jdbcType=VARCHAR}
	
	
		and unit_code = #{unitCode,jdbcType=VARCHAR}
	
	
		and wareh_code = #{warehCode,jdbcType=VARCHAR}
	
	
		and substr(disp_time,1,7) = #{queryMonth,jdbcType=VARCHAR}
	
	
		and substr(disp_time,1,10) = #{queryDay,jdbcType=VARCHAR}
	
  
		and disp_time >= #{dispTime1,jdbcType=VARCHAR}
	
	
		and disp_time <= #{dispTime2,jdbcType=VARCHAR}

select

from mbvendor.vp_gdn
where 1 = 1

	and doc_code = #{docCode,jdbcType=VARCHAR}


	and doc_date >= #{docDate,jdbcType=TIMESTAMP}


	and unit_code = #{unitCode,jdbcType=VARCHAR}


	and wareh_code = #{warehCode,jdbcType=VARCHAR}


	and ttl_qty = #{ttlQty,jdbcType=DECIMAL}


	and disp_mode = #{dispMode,jdbcType=VARCHAR}


	and disp_time >= #{dispTime,jdbcType=TIMESTAMP}


	and disp_reason = #{dispReason,jdbcType=VARCHAR}


	and doc_state = #{docState,jdbcType=VARCHAR}

limit 1
