mapper.select:
  select 
  
  from mbvendor.vp_wareh_prod_tran
  where 1=1
  
    and substr(prod_num,1,6) = #{prodClsNum,jdbcType=VARCHAR}
  
  
    and prod_num = #{prodNum,jdbcType=VARCHAR}
  
  
    and wareh_code = #{warehCode,jdbcType=VARCHAR}
  
  
    and doc_type = #{docType,jdbcType=VARCHAR}
  
  
		and substr(tran_date,1,7) = #{queryMonth,jdbcType=VARCHAR}
	
	
		and substr(tran_date,1,10) = #{queryDay,jdbcType=VARCHAR}
	
  
		and tran_date >= #{tranDate1,jdbcType=VARCHAR}
	
	
		and tran_date <= #{tranDate2,jdbcType=VARCHAR}
	
	order by tran_date desc

  select count(1)
  from mbvendor.vp_wareh_prod_tran
  where 1=1
  
   and substr(prod_num,1,6) = #{prodClsNum,jdbcType=VARCHAR}
  
  
    and prod_num = #{prodNum,jdbcType=VARCHAR}
  
  
    and wareh_code = #{warehCode,jdbcType=VARCHAR}
  
  
    and doc_type = #{docType,jdbcType=VARCHAR}
  
  
		and substr(tran_date,1,7) = #{queryMonth,jdbcType=VARCHAR}
	
	
		and substr(tran_date,1,10) = #{queryDay,jdbcType=VARCHAR}
	
  
		and tran_date >= #{tranDate1,jdbcType=VARCHAR}
	
	
		and tran_date <= #{tranDate2,jdbcType=VARCHAR}
