mapper.select:
	select 
	
	from mbvendor.vp_grn
	where 1=1
	
		and doc_code = #{docCode,jdbcType=VARCHAR}
	
	
		and unit_code = #{unitCode,jdbcType=VARCHAR}
	
	
		and wareh_code = #{warehCode,jdbcType=VARCHAR}
	
	
		and substr(rcpt_time,1,7) = #{queryMonth,jdbcType=VARCHAR}
	
	
		and substr(rcpt_time,1,10) = #{queryDay,jdbcType=VARCHAR}
	
  
		and rcpt_time >= #{rcptTime1,jdbcType=VARCHAR}
	
	
		and rcpt_time <= #{rcptTime2,jdbcType=VARCHAR}
	
	order by rcpt_time desc

  select count(1)
	from mbvendor.vp_grn
	where 1=1
	
		and doc_code = #{docCode,jdbcType=VARCHAR}
	
	
		and unit_code = #{unitCode,jdbcType=VARCHAR}
	
	
		and wareh_code = #{warehCode,jdbcType=VARCHAR}
	
	
		and substr(rcpt_time,1,7) = #{queryMonth,jdbcType=VARCHAR}
	
	
		and substr(rcpt_time,1,10) = #{queryDay,jdbcType=VARCHAR}
	
  
		and rcpt_time >= #{rcptTime1,jdbcType=VARCHAR}
	
	
		and rcpt_time <= #{rcptTime2,jdbcType=VARCHAR}

select

from mbvendor.vp_grn
where 1 = 1

	and doc_code = #{docCode,jdbcType=VARCHAR}


	and doc_date > #{docDate,jdbcType=TIMESTAMP}


	and unit_code = #{unitCode,jdbcType=VARCHAR}


	and wareh_code = #{warehCode,jdbcType=VARCHAR}


	and ttl_qty = #{ttlQty,jdbcType=DECIMAL}


	and rcpt_mode = #{rcptMode,jdbcType=VARCHAR}


	and rcpt_time > #{rcptTime,jdbcType=TIMESTAMP}


	and rcpt_reason = #{rcptReason,jdbcType=VARCHAR}


	and doc_state = #{docState,jdbcType=VARCHAR}

limit 1
